Ability.Lua:
--Misc local hasTarget = UnitExists(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;) local psBuff = UnitBuffID(&quot;Player&quot;,69369) local nsBuff = UnitBuffID(&quot;Player&quot;,132158)
if not (psBuff or nsBuff) then if hasTarget and Enemy and not isDead then local waBuff = select(4,UnitDebuffID(&quot;Target&quot;,113746))
if not waBuff or waBuff &lt; 3 then CastSpellByName(tostring(GetSpellInfo(770))) end end end

local hasTarget = UnitExists(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if hasTarget and Enemy and not isDead then local inKnown = IsSpellKnown(106731) local inStart,inDuration = GetSpellCooldown(106731) local inCD = inStart + inDuration - GetTime()
if inKnown and inCD &lt; 1 then local BS = select(4,UnitBuffID(&quot;player&quot;, 106951)) local Incarnation = UnitBuffID(&quot;Player&quot;, 102543)
if BS ~= nil and Incarnation == nil then CastSpellByName(tostring(GetSpellInfo(106731))) end end end

--Variables local inBG = PQR_Battleground() --Misc local hasTarget = UnitExists(&quot;target&quot;)
if hasTarget and inBG then local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;)
if CP == 5 then CastSpellByName(tostring(GetSpellInfo(22570))) end end

local hasTarget = UnitExists(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;) local inCombat = UnitAffectingCombat(&quot;Player&quot;) or UnitAffectingCombat(&quot;Target&quot;)
if hasTarget and Enemy and not isDead and inCombat then --Variables local HasGlyph = HasGlyph(127540) local HasSR = select(2,HasSR(&quot;Player&quot;)) --Misc Buff&apos;s/Debuff&apos;s/CD&apos;s local thrashBleed = UnitDebuffID(&quot;Target&quot;,106830,&quot;Player&quot;)
if HasGlyph then if not thrashBleed and HasSR then CastSpellByName(tostring(GetSpellInfo(106830))) end elseif not HasGlyph then if not thrashBleed then CastSpellByName(tostring(GetSpellInfo(106830))) end end end

local hasTarget = UnitExists(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if hasTarget and Enemy and not isDead then local nsKnown = IsSpellKnown(132158) local nsStart,nsDuration = GetSpellCooldown(132158) local nsCD = nsStart + nsDuration - GetTime() local HasSR = select(2,HasSR(&quot;Player&quot;))
if nsKnown and nsCD &lt; 1 then local rip, _, _, _, _, _, riptimer = UnitDebuffID(&quot;Target&quot;, 1079, &quot;PLAYER&quot;) local ripCP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;)
if ripCP &gt;= 4 and not rip and (HasSR and HasSR - GetTime() &gt; 5) then CastSpellByName(tostring(GetSpellInfo(132158))) elseif ripCP &gt;= 4 and (riptimer and riptimer - GetTime() &lt; 8) and (HasSR and HasSR - GetTime() &gt; 5) then CastSpellByName(tostring(GetSpellInfo(132158))) end end end
