Ability.Lua:
local hasTarget = UnitExists(&quot;Target&quot;)
if hasTarget == nil then return false else local Hostile = UnitIsEnemy(&quot;Target&quot;, &quot;Player&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if Hostile and not isDead then local playerLevel = UnitLevel(&quot;Player&quot;) local enemyLevel = UnitLevel(&quot;Target&quot;) local facing = PQR_UnitFacing(&quot;Target&quot;, &quot;Player&quot;) if facing == true or PQR_NotBehindTarget() then local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;) if CP &lt; 5 then return true else return false end end end end

local hasTarget = UnitExists(&quot;Target&quot;) local inCombat = UnitAffectingCombat(&quot;Player&quot;) local catForm = UnitBuffID(&quot;Player&quot;, 768)
if hasTarget then local Hostile = UnitIsEnemy(&quot;Target&quot;, &quot;Player&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if Hostile and not isDead and not catForm then CastSpellByName(tostring(GetSpellInfo(768))) elseif inCombat and not catForm then CastSpellByName(tostring(GetSpellInfo(768))) end end

local isKnown = IsSpellKnown(1822) local hasTarget = UnitExists(&quot;Target&quot;) local prowlBuff = UnitBuffID(&quot;Player&quot;, 5215)
if hasTarget and isKnown and not prowlBuff then local rake, _, _, _, _, _, raketimer = UnitDebuffID(&quot;target&quot;, 1822, &quot;PLAYER&quot;)
if rake ~= nil then if raketimer - GetTime() &lt; 4 then return true end else return true end end

local hasTarget = UnitExists(&quot;Target&quot;) local isKnown = IsSpellKnown(9005)
if hasTarget and isKnown then local hasProwl = UnitBuffID(&quot;Player&quot;, 5215) local Hostile = UnitIsEnemy(&quot;Target&quot;, &quot;Player&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if hasProwl and Hostile and not isDead then return true end end

local hasTarget = UnitExists(&quot;Target&quot;)
if hasTarget then local Hostile = UnitIsEnemy(&quot;Target&quot;, &quot;Player&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if Hostile and not isDead then local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;) if CP == 5 then return true end end end

local knownDash = IsSpellKnown(1850) local hastarget = UnitExists(&quot;Target&quot;)
if hasTarget and knownDash then local Hostile = UnitIsEnemy(&quot;Target&quot;, &quot;Player&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if Hostile and not isDead then local chargeCD = GetSpellCooldown(49376) local chargeRange = IsSpellInRange(49376) local mangleRange = IsSpellInRange(33876)
if chargeCD ~= 0 and not mangleRange and chargeRange then return true end end end

local hasTarget = UnitExists(&quot;Target&quot;) local knownSR = IsSpellKnown(52610)
if hasTarget and knownSR then local Hostile = UnitIsEnemy(&quot;Target&quot;, &quot;Player&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if Hostile and not isDead then local playerLevel = UnitLevel(&quot;Player&quot;)
if playerLevel &lt; 85 then local playerHP = UnitHealthMax(&quot;Player&quot;) local targetHP = UnitHealthMax(&quot;Target&quot;)
if targetHP &lt; playerHP*1.2 then local SRbuff = select(4, UnitBuffID(&quot;Player&quot;, 52610)) local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;)
if not SRbuff and CP ~= 0 then return true end elseif targetHP &gt; playerHP*1.5 then local SRbuff = select(4, UnitBuffID(&quot;Player&quot;, 52610)) local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;)
if not SRbuff and CP &gt; 2 then return true end elseif targetHP &gt; playerHP*2.5 then local SRbuff = select(4, UnitBuffID(&quot;Player&quot;, 52610)) local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;)
if not SRbuff and CP &gt; 3 then return true end elseif targetHP &gt; playerHP*4 then local SRbuff = select(4, UnitBuffID(&quot;Player&quot;, 52610)) local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;) local srTimer = select(7,UnitBuffID(&quot;player&quot;, 52610)) local Rip = UnitDebuffID(&quot;Target&quot;, 1079, &quot;PLAYER&quot;)
if not SRbuff and Rip ~= nil then return true elseif srTimer - GetTime() &lt; 4 and CP &gt; 3 then return true elseif srTimer - GetTime() &lt; 2 and CP &lt; 3 then return true end end elseif playerLevel == 85 then local SRbuff = select(4, UnitBuffID(&quot;Player&quot;, 52610)) local CP = GetComboPoints(&quot;Player&quot;, &quot;Target&quot;) local srTimer = select(7,UnitBuffID(&quot;player&quot;, 52610)) local Rip = UnitDebuffID(&quot;Target&quot;, 1079, &quot;PLAYER&quot;)
if not SRBuff and Rip ~= nil then return true elseif srTimer - GetTime() &lt; 4 and CP &gt; 3 then return true elseif srTimer - GetTime() &lt; 2 and CP &lt; 3 then return true end end end end

local hasTarget = UnitExists(&quot;Target&quot;) local knownFF = IsSpellKnown(16857) local prowlBuff = UnitBuffID(&quot;Player&quot;, 5215)
if hasTarget and knownFF and not prowlBuff then local FF, _, _, _, _, _, FFtimer = UnitDebuffID(&quot;target&quot;, 91565) local FFstart, FFduration, FFenabled = GetSpellCooldown(16857) local FFcooldown = (FFstart + FFduration - GetTime())
if FF == nil then if FFcooldown &gt; 0 then return false else return true end end end

local hasTarget = UnitExists(&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;)
if hasTarget and not isDead then local direction = GetEclipseDirection()
if direction == &quot;moon&quot; or direction == &quot;none&quot; then CastSpellByName(tostring(GetSpellInfo(5176))) elseif direction == &quot;sun&quot; then CastSpellByName(tostring(GetSpellInfo(2912))) end end
