Ability.Lua:
local hasTarget = UnitExists(&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;)
if hasTarget and not isDead and Enemy then local rage = UnitPower(&quot;Player&quot;) local playerLevel = UnitLevel(&quot;Player&quot;)
if playerLevel &lt; 14 then if rage &gt;= 20 then return true end elseif playerLevel &gt;= 14 then if rage &gt; 70 then return true end end end

local hasTarget = UnitExists(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local chKnown = IsSpellKnown(100) local isDead = UnitIsDead(&quot;Target&quot;)
if hasTarget and not isDead and Enemy and chKnown then local range = IsSpellInRange(tostring(GetSpellInfo(100)))
if range then return true end end

local hasTarget = UnitExists(&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local rushKnown = IsSpellKnown(34428)
if hasTarget and not isDead and Enemy and rushKnown then local rushCD = GetSpellCooldown(34428)
if rushCD == 0 then return true end end

local hasTarget = UnitExists(&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local rendKnown = IsSpellKnown(772)
if hasTarget and not isDead and Enemy and rendKnown then local rend = UnitDebuffID(&quot;Target&quot;, 94009)
if not rend then return true end end

local hasTarget = UnitExists(&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local tcKnown = IsSpellKnown(6343)
if hasTarget and not isDead and Enemy and tcKnown then local tcBuff = UnitDebuffID(&quot;Target&quot;, 6343) local rage = UnitPower(&quot;Player&quot;)
if not tcBuff and rage &gt; 50 then return true end end

local hasTarget = UnitExists(&quot;Target&quot;) local isDead = UnitIsDead(&quot;Target&quot;) local Enemy = UnitCanAttack(&quot;Player&quot;,&quot;Target&quot;) local hcKnown = IsSpellKnown(78)
if hasTarget and not isDead and Enemy and hcKnown then local rage = UnitPower(&quot;Player&quot;) local playerLevel = UnitLevel(&quot;Player&quot;)
if playerLevel &lt; 85 then if rage &gt;= 30 then return true end end end

